if QuestStatus("r3m2_TwoMagicThing")==Q_TAKEN and vehtype=="ArcadeScout01" then return else TActivate("cinPlTurnRoundFactory_FadeIn") TActivate("cinPlTurnRoundFactory_PathStart") TActivate("cinPlTurnRoundFactory_End")
SaveAllToleranceStatus(RS_NEUTRAL)
if IsQuestTaken("r3m2_TwoMagicThing_Return") then Fly("WentFactory_TurnAround", CINEMATIC_NO_AIM, nil, 4, 1,1) StartCinematic()
AddCinematicMessage(11, 0.25) else Fly("WentFactory_TurnAround", CINEMATIC_NO_AIM, nil, 9, 1,0) Fly("WentFactory_TurnAround2", CINEMATIC_NO_AIM, nil, 8, 0,1) StartCinematic()
TActivate("trExcahgeVehFirst") TActivate("trPlLeaveFactyOnScout") TActivate("cinWentFactory_Path2") AddCinematicMessage(6, 0.25) end end
