elseif QuestStatus("r2m2_MoveToZeonToAlex")==Q_COMPLETED then belong = 1015 towns = {"GranicaVillage"} SetTolerance(1100, 1016, RS_ALLY) CreateNewDummyObject("flagshtock", "GranicaFlag", -1, -1, CVector(1463.337, 296.518, 3759.289), Quaternion(0.0000, -0.1370, 0.0000, 0.9906), 6)
