local len = getn(towns) for i = 1, len do if getObj(towns[i]) then getObj(towns[i]):SetBelong(belong) end if belong ~= 1015 then CreateNewDummyObject("flagshtock", "R2M2Flag"..i, -1, -1, FlagPos[i], FlagRot[i], flag_skin) end end
belong = nil towns = nil flag_skin = nil FlagPos = nil FlagRot = nil
